import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function BookDetail() {
    const { productId } = useParams();
    const navigate = useNavigate();
    const [product, setProduct] = useState(null);

    function updateInventoryItem(itemId, newData) {
        return axios.put(`http://localhost:8000/api/update-inventory/${itemId}/`, newData);
    }

    useEffect(() => {
        fetchProduct();
    }, [productId]);

    const fetchProduct = async () => {
        try {
            const response = await axios.get(`http://localhost:8000/api/inventory/${productId}/`);
            setProduct(response.data);
        } catch (error) {
            console.error('Error fetching product:', error);
        }
    };

    const handleUpdate = async () => {
        try {
            if (!product) {
                console.error('Product data is not available');
                return;
            }

            const response = await updateInventoryItem(productId, product);
            console.log('Item updated successfully:', response.data);
        } catch (error) {
            console.error('Error updating item:', error);
        }
    };

    const handleDelete = async () => {
        try {
            await axios.delete(`http://localhost:8000/api/delete-inventory/${productId}/`);
            // Redirect to the product list after deletion
            navigate('/inventory');
        } catch (error) {
            console.error('Error deleting product:', error);
        }
    };

    if (!product) {
        return <div>No product found</div>;
    }

    return (
        <div className="container">
            <h1 className="my-4">Book Detail</h1>
            <div className="book-detail">
                <h1>
                    <input
                        type="text"
                        value={product.title}
                        onChange={(e) => setProduct({ ...product, title: e.target.value })}
                    />
                </h1>
                <p>
                    Author: 
                    <input
                        type="text"
                        value={product.author}
                        onChange={(e) => setProduct({ ...product, author: e.target.value })}
                    />
                </p>
                <p>
                    Price: 
                    <input
                        type="number"
                        value={product.price}
                        onChange={(e) => setProduct({ ...product, price: e.target.value })}
                    />
                </p>
                <p>
                    Quantity: 
                    <input
                        type="number"
                        value={product.quantity}
                        onChange={(e) => setProduct({ ...product, quantity: e.target.value })}
                    />
                </p>
                {/* Add static image of the product */}
                <div className="product-image">
                    <img src={product.image_url} alt={product.title} />
                </div>
                {/* Buttons for update and delete */}
                <button onClick={handleUpdate}>Update</button>
                <button onClick={handleDelete}>Delete</button>
            </div>
        </div>
    );
}

export default BookDetail;
