import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Modal, Button } from 'react-bootstrap';

function BookList() {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [filter, setFilter] = useState('');
    const [sortedBy, setSortedBy] = useState(null);
    const [newProduct, setNewProduct] = useState({
        title: '',
        author: '',
        category: '',
        price: '',
        quantity: '',
        publication_date: '',
        isbn: '',
    });

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await axios.get('http://localhost:8000/api/inventory/');
            setProducts(response.data);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching products:', error);
            setLoading(false);
        }
    };

    const handleFilterChange = (event) => {
        setFilter(event.target.value);
    };

    const handleSortChange = (event) => {
        setSortedBy(event.target.value);
    };

    const filteredProducts = products.filter(product => {
        return product.title.toLowerCase().includes(filter.toLowerCase());
    });

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setNewProduct({
            ...newProduct,
            [name]: value,
        });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            await axios.post('http://localhost:8000/api/add-inventory/', newProduct);
            setNewProduct({
                title: '',
                author: '',
                category: '',
                price: '',
                quantity: '',
                publication_date: '',
                isbn: '',
            });
            fetchProducts();
        } catch (error) {
            console.error('Error adding product:', error);
        }
    };

    if (sortedBy) {
        filteredProducts.sort((a, b) => {
            if (a[sortedBy] < b[sortedBy]) return -1;
            if (a[sortedBy] > b[sortedBy]) return 1;
            return 0;
        });
    }

    return (
        <div className="container">
            <Button variant="primary" onClick={() => setShowModal(true)}>
                Add Product
            </Button>

            {/* Modal */}
            <Modal show={showModal} onHide={() => setShowModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Add New Product</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form onSubmit={handleSubmit}>
                        <input
                            type="text"
                            name="title"
                            placeholder="Title"
                            value={newProduct.title}
                            onChange={handleInputChange}
                        />
                        {/* Add input fields for other product details */}
                        <button type="submit">Submit</button>
                    </form>
                </Modal.Body>
            </Modal>
            <h1 className="my-4">Book List</h1>

            <div className="mb-3">
                <input
                    type="text"
                    placeholder="Filter by title..."
                    value={filter}
                    onChange={handleFilterChange}
                />
                <select onChange={handleSortChange}>
                    <option value="">Sort by...</option>
                    <option value="title">Title</option>
                    <option value="price">Price</option>

                </select>
            </div>

            {loading ? (
                <div>Loading...</div>
            ) : (
                <table className="table">
                    <thead>
                        <tr>
                            <th>Title</th>
                            <th>ISBN</th>
                            <th>Author</th>
                            <th>Category</th>
                            <th>Price</th>
                            <th>Quantity</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredProducts.map(product => (
                            <tr key={product.id}>
                                <td>{product.title}</td>
                                <td>{product.isbn}</td>
                                <td>{product.author}</td>
                                <td>{product.category}</td>
                                <td>{product.price}</td>
                                <td>{product.quantity}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}

export default BookList;
